package com.xg.navigation.delegates.bottom;

import android.graphics.Bitmap;
import android.graphics.drawable.Drawable;

/**
 * Created by yuanyuan on 18-1-18.
 */

public class BottomTabBean {

    private final Drawable ICON;
    private final Drawable UN_SELECTED_ICON;
    private final CharSequence TITLE;

    public BottomTabBean(Drawable icon, Drawable unSelectedIcon, CharSequence title) {
        this.ICON = icon;
        UN_SELECTED_ICON = unSelectedIcon;
        this.TITLE = title;
    }

    public Drawable getIcon() {
        return ICON;
    }

    public CharSequence getTitle() {
        return TITLE;
    }

    public Drawable getUnSelectedIcon() {return UN_SELECTED_ICON;}
}
