package com.xg.navigation.util;

import android.os.Bundle;
import android.util.Log;

import java.util.Set;

/**
 * Created by yuanyuan on 18-1-24.
 */

public class BundleUtil {

    public static void printBundleInfo(Bundle bundle) {
        if (bundle == null) {
            return;
        }

        Set<String> keys = bundle.keySet();
        for (String key: keys) {
            Log.d("spy", "key: " + key + ", value: " + bundle.get(key).toString() + ", type: " + bundle.get(key).getClass().getCanonicalName());
        }
        Log.d("spy", "END============================================");
        Log.d("spy", "");
    }
}
