#import "UIView+XGCallBack.h"
#import <objc/runtime.h>

@implementation UIView (XGCallBack)

static const char XGCallBackID = '\0';
- (void)setXg_callBackArr:(NSMutableArray *)xg_callBackArr{
    [self willChangeValueForKey:@"xg_callBackArr"]; // KVO
    objc_setAssociatedObject(self, &XGCallBackID,
                             xg_callBackArr, OBJC_ASSOCIATION_RETAIN);
    [self didChangeValueForKey:@"xg_callBackArr"]; // KVO
}

- (NSMutableArray*)xg_callBackArr{
    return objc_getAssociatedObject(self, &XGCallBackID);
}


@end
