#import "XGRNViewFactory.h"
#import <React/RCTRootView.h>
#import "XGRNBridgeManager.h"

@implementation XGRNViewFactory


+ (UIView *)getAlertByConfig:(NSDictionary *)alertConfig{
    RCTBridge * bridge = [[XGRNBridgeManager standardManager] getCommonBridge];
    return [[RCTRootView alloc] initWithBridge:bridge moduleName:@"alert" initialProperties:alertConfig];
}

@end
