package com.xinguang.imaglib.cycleImage;

import android.content.Context;
import android.support.annotation.AttrRes;
import android.support.annotation.NonNull;
import android.support.annotation.Nullable;
import android.util.AttributeSet;
import android.view.LayoutInflater;
import android.view.View;
import android.widget.FrameLayout;

import com.xinguang.imaglib.cycleImage.view.UI;

/**
 * 简化自定义view流程
 *
 * @author wl on 2017/8/10 0010 15:35
 */

public abstract class BaseFrameLayout extends FrameLayout implements UI {
    protected Context context;

    public BaseFrameLayout(@NonNull Context context) {
        this(context, null);
    }

    public BaseFrameLayout(@NonNull Context context, @Nullable AttributeSet attrs) {
        this(context, attrs, 0);
    }

    public BaseFrameLayout(@NonNull Context context, @Nullable AttributeSet attrs,
                           @AttrRes int defStyleAttr) {
        super(context, attrs, defStyleAttr);
        this.context = context;
        init();
        initData();
    }


    protected void setContentView(int id) {
        LayoutInflater.from(context).inflate(id, this);
    }

    protected <T extends View> T getView(int resId) {
        return (T) findViewById(resId);
    }

    @Override
    public abstract void init();

    @Override
    public abstract void initData();
}
