package com.xinguang.imaglib.cycleImage.bean;

/**
 * 携带item 数据 和position
 *
 * @author wl on 2017/8/16 0016 16:56
 */

public class RecycleViewBean<T> {
    public T titleBean;
    public int position;

    public RecycleViewBean(T titleBean, int position) {
        this.titleBean = titleBean;
        this.position = position;
    }

    public RecycleViewBean(T titleBean) {
        this.titleBean = titleBean;
        this.position = 0;
    }

    public T getTitleBean() {
        return titleBean;
    }

    public int getPosition() {
        return position;
    }
}
