package com.xinguang.imaglib.cycleImage.cycle;

import android.content.Context;
import android.support.annotation.NonNull;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import com.xinguang.imaglib.cycleImage.bean.RecycleViewBean;
import com.xinguang.imaglib.cycleImage.utils.GlideS;
import com.xinguang.imaglib.cycleImage.view.BaseItemVideoView;


/**
 * SeasonFragment  view
 * 图片类型的
 *
 * @author wl on 2017/8/14 0014 16:00
 */

public class DetailsSeasonView extends BaseItemVideoView<VideoImageBean> {
    ImageView iv_item_view_season_image;

    public DetailsSeasonView(@NonNull Context context) {
        super(context);
    }

    @Override
    public void init() {
        iv_item_view_season_image = new ImageView(context);
        iv_item_view_season_image.setLayoutParams(
                new ViewGroup.LayoutParams(ViewGroup.LayoutParams.MATCH_PARENT,
                        ViewGroup.LayoutParams.MATCH_PARENT));
        iv_item_view_season_image.setScaleType(ImageView.ScaleType.FIT_CENTER);
        addView(iv_item_view_season_image);
    }

    @Override
    public void initData() {
        setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                CycleViewZoomManager.getInstance().switchContainer();
            }
        });
    }

    @Override
    public void setData(RecycleViewBean<VideoImageBean> object) {
        GlideS.resultBase( object.getTitleBean().getPath(),iv_item_view_season_image);
    }
}
