package com.xinguang.imaglib.cycleImage.cycle;

/**
 * 图片视频混排bean
 *
 * @author wl on 2017/8/18 0018 14:11
 */

public class VideoImageBean {
    int type;// 0  图片  1 视频
    String path;//图片或视频的地址
    //其它的先不要
    String pic;

    public VideoImageBean(int type, String path, String pic) {
        this.type = type;
        this.path = path;
        this.pic = pic;
    }

    public VideoImageBean(int type, String path) {
        this.type = type;
        this.path = path;

    }

    public int getType() {
        return type;
    }

    public String getPath() {
        return path;
    }

    public String getPic() {
        return pic;
    }
}
