package com.xinguang.imaglib.cycleImage.view;

import android.content.Context;
import android.support.annotation.AttrRes;
import android.support.annotation.NonNull;
import android.support.annotation.Nullable;
import android.util.AttributeSet;

import com.xinguang.imaglib.cycleImage.BaseFrameLayout;
import com.xinguang.imaglib.cycleImage.bean.RecycleViewBean;

/**
 * item view
 *
 * @author wl on 2017/8/14 0014 18:30
 */

public abstract class BaseItemView<T extends Object> extends BaseFrameLayout {
    protected int position;
    protected T titleBean;

    public BaseItemView(@NonNull Context context) {
        super(context);

    }

    public BaseItemView(@NonNull Context context, @Nullable AttributeSet attrs) {
        super(context, attrs);
    }

    public BaseItemView(@NonNull Context context, @Nullable AttributeSet attrs,
                        @AttrRes int defStyleAttr) {
        super(context, attrs, defStyleAttr);
    }


    /**
     * 都有一个设置数据的方法
     */
    public void setData(RecycleViewBean<T> object) {
        position = object.position;
        if ((titleBean=object.titleBean) != null) {
            setData(titleBean);
        }

    }

    protected void setData(T object) {
    }


}
