package com.xinguang.imaglib.imageviewermodule;

import android.content.Intent;

import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactMethod;
import com.facebook.react.bridge.ReadableMap;

import java.util.ArrayList;

/**
 * Created by lingwang on 2018/4/23.
 */

public class ImageViewerModule extends ReactContextBaseJavaModule {
    ReactApplicationContext context;

    public ImageViewerModule(ReactApplicationContext reactContext) {
        super(reactContext);
        context = reactContext;
    }

    @Override
    public String getName() {
        return "XGOAPhotoBrowserTool";
    }

    // imageUrls:[urls],localImages:[Image],startIndex:NSInteger
    @ReactMethod
    public void showPhotoBrowserWithParams(ReadableMap params) {
        ArrayList<Object> imageUrls = params.getArray("imageUrls").toArrayList();
        int startIndex = Integer.valueOf(params.getString("startIndex"));
        ArrayList<String> kkk = new ArrayList<>();
        for (Object data : imageUrls) {
            kkk.add(data.toString());
        }
//        ArrayList<String> kkk = new Gson().fromJson(imageUrls.toString(), new TypeToken<List<String>>() {
//        }.getType());
        Intent intent = new Intent(context, ImageViewerActivity.class);
        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
        intent.putStringArrayListExtra("videos", null);
        intent.putStringArrayListExtra("pics", kkk);
        intent.putExtra("address", "");
        intent.putExtra("position", startIndex);
        context.startActivity(intent);
    }
}
